const request = require('request');

const options = {
  method: 'POST',
  url: 'http://localhost:3000/api/analyzeMessage',
  headers: {'content-type': 'application/json'},
  body: {
    tenantId: 'zxyorg.slack.com',
    teamId: 'testteam_20220219_03',
    registryUserId: 'usr09871',
    email: 'jennygraham@myorg.com',
    conversationType: 'channel',
    messageText: 'This is a regular message',
    files: [
      {contentUrl: 'https://xyzorg.com/media/abc123.png', authToken: 'juy_shq16$8998'}
    ]
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});
